import React, { Component } from 'react';
import {
    Modal,
    ModalHeader,
    ModalBody
} from 'reactstrap';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import history from '../../Services/history';
import AuthActions from '../../Redux/CustomerRedux';
import CustomerListComponent from '../../Components/Customer/CustomerListComponent';
import CustomerFormComponent from '../../Components/Customer/CustomerFormComponent';

class CustomerContainer extends Component {
    state = {
        modalCreateCustomer: false,
        titleModal: 'Agregar cliente',
        customerToEdit: null,
    }
    componentDidMount = () => {
        //console.log('', this.props)
        this.props.actions.customer.fetchCustomers();
    }
    componentDidUpdate = (prevProps) => {
        if (!prevProps.service.serviceCreated && this.props.service.serviceCreated ||
            !prevProps.service.serviceUpdated && this.props.service.serviceUpdated ||
            !prevProps.service.serviceDeleted && this.props.service.serviceDeleted) {
                this.props.actions.service.fetchServices();
                this.setState({
                    modal: false,
                })
        } else if (!prevProps.service.serviceToEdit && this.props.service.serviceToEdit) {
            this.setState({
                serviceToEdit: this.props.service.serviceToEdit,
            }, () => {
                this.toggle();
            })
        }
    }
    toggleConfirm = () => {
        this.setState({ modalCreateCustomer: !this.state.modalCreateCustomer });
    }
    getCustomer = (id) => {
        this.props.actions.customer.fetchCustomer(id);
    }
    render() {
        return (
            <div>
                <Modal isOpen={this.state.modalCreateCustomer} className={'modal-success'} toggle={this.toggleConfirm}>
                    <ModalHeader toggle={this.toggleConfirm}>{this.state.titleModal}</ModalHeader>
                    <ModalBody>
                        <CustomerFormComponent
                            onAccept={this.createCustomer}
                            onCancel={this.toggleConfirm}
                            customerToEdit={this.state.customerToEdit}
                        />
                    </ModalBody>
                </Modal>
                <CustomerListComponent
                    onOpenModal={this.toggleConfirm}
                    customers={this.props.customer.customers} />
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
    customer: state.customer,
});

const mapDispatchToProps = (dispatch) => ({
    actions: {
        customer: bindActionCreators(AuthActions, dispatch),
    },
});

export default connect(mapStateToProps, mapDispatchToProps)(CustomerContainer);
