import React, { Component } from 'react';
import {
    Row, Col,
    Card, CardBody,
    ButtonGroup, ButtonDropdown,
    DropdownToggle, DropdownMenu,
    DropdownItem
} from 'reactstrap';
import SaleListComponent from '../../Components/Sale/SaleListComponent';

class SaleContainer extends Component {
    state = {
        modalCreateSale: false,
        titleModal: 'Agregar Proveedor',
    }
    toggleConfirm = () => {
        this.setState({ modalCreateSale: !this.state.modalCreateSale });
    }
    render() {
        return (
            <div>
                <Row>
                    <Col md="3">
                            <Card className="text-white bg-info">
                                <CardBody className="pb-0">
                                    <ButtonGroup className="float-right">
                                        <ButtonDropdown id='card1' isOpen={this.state.card1} toggle={() => { this.setState({ card1: !this.state.card1 }); }}>
                                            <DropdownToggle caret className="p-0" color="transparent">
                                                <i className="icon-settings"></i>
                                            </DropdownToggle>
                                            <DropdownMenu right>
                                                <DropdownItem>Action</DropdownItem>
                                                <DropdownItem>Another action</DropdownItem>
                                                <DropdownItem disabled>Disabled action</DropdownItem>
                                                <DropdownItem>Something else here</DropdownItem>
                                            </DropdownMenu>
                                        </ButtonDropdown>
                                    </ButtonGroup>
                                    <div className="text-value">9.823</div>
                                    <div>Members online</div>
                                </CardBody>
                            </Card>
                    </Col>
                    <Col md="9">
                        <SaleListComponent
                            onOpenModal={this.toggleConfirm} />
                    </Col>
                </Row>
            </div>
        )
    }
}

export default SaleContainer;
