import React, { Component } from 'react';
import {
    Row, Col,
    Card, CardBody,
    ButtonGroup, ButtonDropdown,
    DropdownToggle, DropdownMenu,
    DropdownItem
} from 'reactstrap';
import SaleListComponent from '../../Components/Sale/SaleListComponent';

class SaleContainer extends Component {
    state = {
        modalCreateSale: false,
        titleModal: 'Agregar Proveedor',
    }
    toggleConfirm = () => {
        this.setState({ modalCreateSale: !this.state.modalCreateSale });
    }
    render() {
        return (
            <div>
                <Row>
                    <Col md="3">
                        <Row>
                            <Col md="12">
                                <Card className="text-white bg-info">
                                    <CardBody>
                                        <div>NÂ° Ventas 2019</div>
                                        <div className="text-value">9.823</div>
                                    </CardBody>
                                </Card>
                            </Col>
                            <Col md="12">
                                <Card className="text-white bg-info">
                                    <CardBody>
                                        <div>Ganancias 2019</div>
                                        <div className="text-value">9.823</div>
                                    </CardBody>
                                </Card>
                            </Col>
                        </Row>
                    </Col>
                    <Col md="9">
                        <SaleListComponent
                            onOpenModal={this.toggleConfirm} />
                    </Col>
                </Row>
            </div>
        )
    }
}

export default SaleContainer;
